function OnKill(event, player, creature)
local gold = 10000
local entry = creature:GetEntry()


for k,v in pairs(BossID) do
	if entry == v then
	
	local group = player:GetGroup()
	
	if group ~= nil then
	local groupPlayers = group:GetMembers()
	for k,v in pairs(groupPlayers) do
	--v:ModifyMoney( gold*5 )
	v:ModifyHonorPoints( 100 )
	v:ModifyArenaPoints( 100 )
	end
	end
	end
	end
end


